'use strict';

var define = require('define-properties');
var callBind = require('call-bind');

var implementation = require('function.prototype.name/implementation');
var getPolyfill = require('function.prototype.name/polyfill');
var shim = require('function.prototype.name/shim');

var bound = callBind(implementation);

define(bound, {
	getPolyfill: getPolyfill,
	implementation: implementation,
	shim: shim
});

module.exports = bound;
